var assert = require("assert")
var base64Clean = require("../main");

var testString = "";


describe("Standard:", function(){
	
	it("base64", function(){
		var options = { variant: "base64" };

		var data = "!@#$$%#^^$%" + testString;

		var result = base64Clean(options, data);
		assert.equal(testString, result);
	});

});


describe("Curried:", function(){
	
	it("base64", function(){
		var options = { variant: "base64" };
		var data = "!@#$$%#^^$%" + testString;
		var b64 = base64Clean(options);

		var result = b64(data);
		assert.equal(testString, result);
	});

});
