/*
	string-segment
*/

var R = require("ramda");


/*
	Functionality
*/

var main = R.curry(function (length, data) {
	if (!length) return [data];
	return R.match(
		new RegExp(".{1," + length + "}", "g")
	)(data);
});


/*
	Exports
*/

if (typeof module !== "undefined") {
	module.exports = main;
}
