var assert = require("assert")
var stringSegment = require("../main");

var testString = "aaaaabbbbbcccccdddddeeeee";


describe("Standard:", function(){

	it("length = 0", function(){
		var expect = [testString];
		var result = stringSegment(0, testString);
		assert.deepEqual(expect, result);
	});
	
	it("length = 5", function(){
		var expect = ["aaaaa", "bbbbb", "ccccc", "ddddd", "eeeee"];
		var result = stringSegment(5, testString);
		assert.deepEqual(expect, result);
	});

	it("length = 10", function(){
		var expect = ["aaaaabbbbb", "cccccddddd", "eeeee"];
		var result = stringSegment(10, testString);
		assert.deepEqual(expect, result);
	});

});


describe("Curried:", function(){

	it("length = 5", function(){
		var expect = ["aaaaa", "bbbbb", "ccccc", "ddddd", "eeeee"];
		var segment5 = stringSegment(5);
		var result = segment5(testString);
		assert.deepEqual(expect, result);
	});

});
